CREATE TABLE Province (
  id CHAR(2) PRIMARY KEY,
  nameEn TEXT,
  nameFr TEXT,
  sourceLink TEXT DEFAULT null,
  sourceEn TEXT DEFAULT null
);

CREATE TABLE Holiday (
	id INTEGER PRIMARY KEY AUTOINCREMENT,
	date TEXT,
	nameEn TEXT,
	nameFr TEXT,
	federal BOOLEAN DEFAULT FALSE,
  firstOccurence TEXT DEFAULT null,
  lastOccurence TEXT DEFAULT null
);

CREATE TABLE ProvinceHoliday (
  id INTEGER PRIMARY KEY,
  provinceId CHAR(2),
  holidayId INTEGER,
  optional BOOLEAN DEFAULT FALSE,
  FOREIGN KEY(provinceId) REFERENCES Province(id),
  FOREIGN KEY(holidayId) REFERENCES Holiday(id)
);


